package com.example.room_mvvm;import android.content.Intent;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import androidx.lifecycle.Observer;import androidx.lifecycle.ViewModelProviders;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import com.google.android.material.floatingactionbutton.FloatingActionButton;import java.util.List;public class MainActivity extends AppCompatActivity {    NoteViewModel noteViewModel;    RecyclerView recyclerView;    NoteAdapter noteAdapter;    FloatingActionButton fab;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        recyclerView = findViewById(R.id.recyclerview);        fab = findViewById(R.id.fab);        fab.setOnClickListener(v->{            Intent i = new Intent(this,AddNoteActivity.class);            startActivity(i);        });        recyclerView.setLayoutManager(new LinearLayoutManager(this));        recyclerView.setHasFixedSize(true);        noteAdapter = new NoteAdapter();        recyclerView.setAdapter(noteAdapter);        noteViewModel = ViewModelProviders.of(this).get(NoteViewModel.class);        noteViewModel.getAllNotes().observe(this, new Observer<List<Note>>() {            @Override            public void onChanged(List<Note> notes) {                noteAdapter.setNotes(notes);            }        });    }}