package com.example.room_mvvm;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.ArrayList;import java.util.List;public class NoteAdapter extends RecyclerView.Adapter<NoteAdapter.NoteHolder> {    private List<Note> noteList = new ArrayList<>();    @NonNull    @Override    public NoteHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View itemView = LayoutInflater.from(parent.getContext()).inflate(R.layout.note_item, parent, false);        return new NoteHolder(itemView);    }    @Override    public void onBindViewHolder(@NonNull NoteHolder holder, int position) {        Note currentNote = noteList.get(position);        holder.title.setText(currentNote.getTitle());        holder.priority.setText(currentNote.getPriority() + "");        holder.description.setText(currentNote.getDescription());    }    @Override    public int getItemCount() {        return noteList.size();    }    public void setNotes(List<Note> notes) {        this.noteList = notes;        notifyDataSetChanged();    }    public Note getNoteAt(int position) {        return noteList.get(position);    }    class NoteHolder extends RecyclerView.ViewHolder {        private TextView title, description, priority;        public NoteHolder(@NonNull View itemView) {            super(itemView);            title = itemView.findViewById(R.id.textview_title);            description = itemView.findViewById(R.id.description);            priority = itemView.findViewById(R.id.text_view_priority);        }    }}